<style>
@keyframes wobble {
    from {
        transform: translateY(-6px);
    }
    to {
        transform: translateY(6px);
    }
}

.book-animation-stage {
    max-width: 300px;
    margin: 0 auto;
    padding-bottom: 1rem;
}

#book {
    overflow: visible;
}

#book * {
    transition: all 0.2s;
}

#book path,
#book circle {
    fill: var(--the-yellow);
}

#bobbles {
    opacity: 0;
}

#hand {
    transform: scale(0.6);
    transform-origin: center;
}

.game-container:hover #bobbles {
    opacity: 1;
}

.game-container:hover #bobbles path,
.game-container:hover #bobbles circle {
    animation: wobble 1s infinite alternate-reverse;
}

.game-container:hover #hand {
    transform: scale(1);
}

</style>