<script>
export default {
    name: 'candle-animation',
    methods: {
        getRandomInt(max) {
            return Math.floor(Math.random() * max);
        }
    }
}
</script>

<style>
.candle-animation * {
    transition: all 0.2s;
}

.candle-animation path {
    fill: var(--the-yellow);
}

#candle-anim {
    max-width: 70px;
    transform: translateY(-30px);
    opacity: 0;
    margin-top: 70px;
}

#flame-2 {
    opacity: 0;
}

@keyframes flicker {
  0%   { opacity:1; }
  50%  { opacity:0; }
  100% { opacity:1; }
}

@keyframes wobble {
    from {
        transform: translateY(-5px);
    }
    from {
        transform: translateY(5px);
    }
}

.remote-viewing-container:hover #candle-anim {
    animation-name: wobble;
    animation-duration: 2s;
    animation-iteration-count: infinite;
    animation-direction: alternate-reverse;
    opacity: 1;
}

.remote-viewing-container:hover #flame-1,
.remote-viewing-container:hover #flame-2 {
    animation-name: flicker;
    animation-duration: 2s;
    animation-iteration-count: infinite;
    animation-direction: alternate-reverse;
}
</style>
