<style>
.window-animation * {
    transition: all 0.2s;
}

.window-animation path {
    fill: var(--the-yellow);
    stroke: var(--the-yellow);
}

#moon-and-clouds {
    opacity: 0;
}

@keyframes orble {
    from {
        transform: scale(0.85);
    }
    to {
        transform: scale(1.05);
    }
}

#sun-proper {
    animation: orble 2s infinite alternate-reverse;
    transform-origin: center;
}

.window-anim-stage:hover #sun-and-clouds {
    opacity: 0;
    transform: translateY(50px);
}

.window-anim-stage:hover #moon-and-clouds {
    opacity: 1;
}
</style>
